import React from  'react';
const Color = (props) => {
    let s =Array.from(props.sm);
    return(
        <>
            <div style={{display:'flex'}}>
                {s.map(
                    ( item ,index )=>{
                        let arr=[];
                    for(let i=1;i <= item.number;i++){
                        arr.push(<div className="box"  style={{backgroundColor:item.color,width:"100px",height:"100px"}}>
                        </div>)
                    }
                    return arr;
                })}
                </div>
        </>
    );
}
export default Color
